public class MIO {
	
	/**
	 * 控制台打印输出对象信息，不换行
	 * @param o
	 */
	public static void print(Object o){
		System.out.print(o);
	}
	
	/**
	 * 打印输出对象信息，换行
	 * @param o
	 */
	public static void println(Object o){
		System.out.println(o);
	}
	
	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

	}
	@Override
	public String toString() {
		// TODO Auto-generated method stub
		return super.toString();
	}

}
